console.log('ajax');$(document).ready(function(){$('body').append('<div id="spinner"><div class="d-flex h-100 align-items-center"><div class="d-block w-100 text-center"><i class="fa fa-spinner fa-pulse fa-2x fa-fw"></i></div></div></div>');$spinner=$('#spinner');$(function(){$('#product_form').on('submit',function(e){$spinner.fadeIn();var form=$(this);var post_url=form.attr('action');var post_data=form.serialize();e.preventDefault();$.ajax({type:'POST',url:post_url,data:post_data,success:function(data){console.log('success');$spinner.fadeOut()}})})})})